import clarkson from '../../Pictures/clarkson-meme.gif';
import john from '../../Pictures/john-meme.gif';
import lehr from '../../Pictures/lehr-meme.png';

import React, { useState } from "react";

import "./PageNotFound.css";

export default function PageNotFound() {
    const RandVal = () => { return Math.random() * 3 + 1; }
    const RevealImage = (val) => { 
      setNum(val) 
        switch(num){
          case 1:
            setMeme(clarkson);
            break;
          case 2:
            setMeme(john);
            break;
          case 3:
            setMeme(lehr);
            break;
          default:
            console.log("Error!");
            break;  
        }
    }

    const [meme, setMeme] = useState(RevealImage(RandVal()));

    return (
      <div className='screen-format'>
        <h1> 404 - Not Found! </h1>
        {meme && <img className='image-style, pop-out' src={meme} alt="clarckson-meme" />}

        <h1> You've entered an invalid path! <br/> Et tu brute?!!! Fix it! </h1>
        <br/>
      </div>
    )
  }
