// 404 Memes - Visual Messages for Users
import clarkson from '../../Pictures/clarkson-meme.gif';
import john from '../../Pictures/john-meme.gif';
import lehr from '../../Pictures/lehr-meme.png';

import React, { useState } from "react";

import "./PageNotFound.css";

export default function PageNotFound() {
    const [num, setNum] = useState(0);
    const [meme, setMeme] = useState(lehr);

    const RandVal = () => { return Math.random() * 3 + 1; }
    const UpdateImage = () => { setNum(Math.floor(RandVal())) }



    return (
      <div className='screen-format'>
        <h1> 404 - Not Found! </h1>
        <img className='image-style, pop-out' src={meme} alt="clarckson-meme" />
        {/* <Confetti /> */}
        <h1> You've entered an invalid path! <br/> Et tu brute?!!! Fix it! </h1>
        <h1> Number is: {num}  </h1>
        <button onClick={UpdateImage}> Update Meme </button>
        <br/>
        <br/>
      </div>
    )
  }
