import React, { useState } from 'react';
import axios from 'axios';

const CHATGPT_API_URL = 'https://api.openai.com/v1/chat/completions'; 

require('dotenv').config();
const apiKey = process.env.API_KEY;

const ChatGPT = () => {
  const [userInput, setUserInput] = useState('');
  const [chatGPTResponse, setChatGPTResponse] = useState('');

  const generateChatGPTResponse = async () => {
    try{
        const response = await axios.post(
            CHATGPT_API_URL,
            {
                model: 'gpt-3.5-turbo', 
                messages: [
                { role: 'system', content: 'You are a helpful assistant.',},
                { role: 'user', content: userInput, },
              ],
            }, {
                headers: {
                    'Content-Type' : 'appllication/json',
                    'Authorization' : apiKey,
                },
                resolve: {
                    fallback: {
                      "path": require.resolve("path-browserify"),
                      "os": require.resolve("os-browserify/browser"),
                      "crypto": require.resolve("crypto-browserify")
                    }
                },
            },
        );
        setChatGPTResponse(response.data.choices[0].message.content);
    } catch (error){
        console.error('Error generating ChatGPT response:', error.response ? error.response.data : error.message);
    }
  }

  return (
    <div>
      <h1>ChatGPT React Example</h1>
      <div>
        <label>User Input:</label>
        <input
          type="text"
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
        />
      </div>
      <button onClick={generateChatGPTResponse}>Generate Response</button>
      <div>
        <h3>ChatGPT Response:</h3>
        <p>{chatGPTResponse}</p>
      </div>
    </div>
  );
};

export default ChatGPT;
